#ifndef LIBGPIO_PIGPIOD_IF2_SWITCH
#define LIBGPIO_PIGPIOD_IF2_SWITCH

#include <libgpio/pigpiod_if2/Device.hpp>
#include <libgpio/PullDirection.hpp>

namespace _libgpio
{
namespace pigpiod_if2
{
    
class Switch : public Device
{
public:
    Switch(uint32_t gpioPin, libgpio::PullDirection pullDirection);
    ~Switch() = default;

    /// @brief Get the input generated by the switch
    /// @param value true = HIGH, false = LOW
    bool getInput() const;

private:
    void configurePull();
    void configureMode();

    uint32_t m_gpioPin;
    libgpio::PullDirection m_pullDirection;
};

} // namespace pigpiod_if2
} // namespace _libgpio

#endif // LIBGPIO_PIGPIOD_IF2_SWITCH
